mapper.select:
select
id, product_id, num, user_id, create_time, status
from ordertb


        and id = #{id}


        and product_id = #{productId}


        and num = #{num}


        and user_id = #{userId}


        and create_time = #{createTime}


        and status = #{status}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from ordertb


        and id = #{id}


        and product_id = #{productId}


        and num = #{num}


        and user_id = #{userId}


        and create_time = #{createTime}


        and status = #{status}
